import React, { useState, useEffect } from "react";
import Artists from "../components/Artists";
import Library from "./Library";
import Register from "./Register";

export default function Home() {
  const [isOpen, setIsOpen] = useState(false);
  const [currentPage, setCurrentPage] = useState("home");
  const [selectedArtist, setSelectedArtist] = useState(null);

  const artists = [
    { nombre: "Christian Nodal", img: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSxKG4q2Nwmq3QmR1u4gmuHyRY1OCGD-b54RHU0frtw10pPgU4SgZuoSq1VsE2Cd49xJoKSEXDyswLJU0ZB6m-0PL8GAP-vv52j9dF9LX5oRA" },
    { nombre: "Karol G", img: "data:image/webp;base64," },
    { nombre: "Drake", img: "data:image/webp;base64," },
    { nombre: "Selena", img: "data:image/webp;base64," },
    { nombre: "Adele", img: "data:image/webp;base64," },
    { nombre: "Belinda", img: "data:image/webp;base64," },
  ];

  const songsByArtist = {
    "Christian Nodal": ["Botella Tras Botella", "De Los Besos Que Te Di", "Ya No Somos Ni Seremos"],
    "Karol G": ["Provenza", "Tusa", "Bichota"],
    "Drake": ["God's Plan", "In My Feelings", "Hotline Bling"],
    "Selena": ["Bidi Bidi Bom Bom", "Como La Flor", "Amor Prohibido"],
    "Adele": ["Easy On Me", "Someone Like You", "Hello"],
    "Belinda": ["Ángel", "Bella Traición", "Luz Sin Gravedad"],
    "Bad Bunny": ["Tití Me Preguntó", "Ojitos Lindos", "Me Porto Bonito"]
  };

  const handleNavigation = (page) => {
    setCurrentPage(page);
    setIsOpen(false);
    if (page !== "library") setSelectedArtist(null);
  };

  const handleArtistClick = (artist) => {
    setSelectedArtist(artist);
    setCurrentPage("library");
  };

  const renderPage = () => {
    switch (currentPage) {
      case "home":
        return (
          <>
            <section className="py-12 text-center bg-gradient-to-b from-zinc-800 to-black">
              <h2 className="mb-4 text-4xl font-extrabold">✨ Bienvenido a tu música ✨</h2>
              <p className="text-lg text-gray-300">
                Descubre, guarda y disfruta tus canciones favoritas
              </p>
            </section>
            <div className="px-6 mx-auto max-w-7xl">
              <Artists artists={artists} handleArtistClick={handleArtistClick} />
              <Library songs={songsByArtist} selectedArtist={null} />
            </div>
          </>
        );
      case "artists":
        return <div className="px-6 mx-auto max-w-7xl">
          <Artists artists={artists} handleArtistClick={handleArtistClick} />
        </div>;
      case "library":
        return <div className="px-6 mx-auto max-w-7xl">
          <Library songs={songsByArtist} selectedArtist={selectedArtist} />
        </div>;
      case "register":
        return <div className="px-6 mx-auto max-w-7xl">
          <Register />
        </div>;
      default:
        return null;
    }
  };

  return (
    <div className="min-h-screen text-white bg-black">
      <nav className="sticky top-0 z-10 flex items-center justify-between p-4 shadow-md bg-zinc-900">
        <h1 onClick={() => handleNavigation("home")} className="text-2xl font-bold cursor-pointer hover:text-green-500">🎵 MySpotify</h1>
        <ul className="hidden space-x-6 text-lg md:flex">
          <li className="cursor-pointer hover:text-green-500" onClick={() => handleNavigation("artists")}>Artistas</li>
          <li className="cursor-pointer hover:text-green-500" onClick={() => handleNavigation("library")}>Librería</li>
        </ul>
        <button className="md:hidden focus:outline-none" onClick={() => setIsOpen(!isOpen)}>
          <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d={isOpen ? "M6 18L18 6M6 6l12 12" : "M4 6h16M4 12h16M4 18h16"} />
          </svg>
        </button>
      </nav>

      {isOpen && (
        <ul className="absolute z-10 flex flex-col w-full px-4 py-2 space-y-2 bg-zinc-900 md:hidden">
          <li className="cursor-pointer hover:text-green-500" onClick={() => handleNavigation("artists")}>Artistas</li>
          <li className="cursor-pointer hover:text-green-500" onClick={() => handleNavigation("library")}>Librería</li>
        </ul>
      )}

      <main>{renderPage()}</main>
    </div>
  );
}
